from MontyCloud.app.list_image import ListImagefrom MontyCloud.app.image_upload import UploadImagefrom MontyCloud.app.download_image import DownloadViewImagefrom MontyCloud.app.delete_image import DeleteImageclass ImageFactory():    def __init__(self, event, context):        self.event = event        self.context = context    def get_object(self, operation):        if operation == "upload_image":            return UploadImage(self.event, self.context)        if operation == "list_image":            return ListImage(self.event, self.context)        if operation == "download_image":            return DownloadViewImage(self.event, self.context)        if operation == "delete_image":            return DeleteImage(self.event, self.context)        return None